import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
    Container,
    Box,
    TextField,
    Button,
    Typography,
    Paper,
    Alert,
    CircularProgress,
    Tabs,
    Tab
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { styled } from '@mui/material/styles';

const BackgroundImage = styled(Box)(({ theme }) => ({
    position: 'relative',
    minHeight: '100vh',
    backgroundImage: 'url(https://images.unsplash.com/photo-1607083206968-13611e3d76db?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80)',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        backdropFilter: 'blur(5px)',
    },
}));

const LoginCard = styled(Paper)(({ theme }) => ({
    position: 'relative',
    padding: theme.spacing(4),
    backgroundColor: 'rgba(30, 30, 30, 0.9)',
    backdropFilter: 'blur(10px)',
    borderRadius: '16px',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
    width: '100%',
    maxWidth: '400px',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    '& .MuiOutlinedInput-root': {
        backgroundColor: 'rgba(255, 255, 255, 0.05)',
        color: '#ffffff',
        '& fieldset': {
            borderColor: 'rgba(255, 255, 255, 0.1)',
        },
        '&:hover fieldset': {
            borderColor: 'rgba(255, 255, 255, 0.2)',
        },
        '&.Mui-focused fieldset': {
            borderColor: theme.palette.primary.main,
        },
    },
    '& .MuiInputLabel-root': {
        color: 'rgba(255, 255, 255, 0.7)',
    },
    '& .MuiInputBase-input': {
        color: '#ffffff',
    },
}));

const Login = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        email: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { login, user } = useAuth();
    const navigate = useNavigate();

    // Redirect if already logged in
    useEffect(() => {
        if (user) {
            navigate('/chat');
        }
    }, [user, navigate]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            if (isLogin) {
                await login(formData.username, formData.password);
                navigate('/chat');
            } else {
                const response = await fetch('http://localhost:5000/api/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(formData),
                });

                if (!response.ok) {
                    const data = await response.json();
                    throw new Error(data.error || 'Registration failed');
                }

                // After successful registration, log in the user
                await login(formData.username, formData.password);
                navigate('/chat');
            }
        } catch (err) {
            setError(err.message || 'An error occurred');
        } finally {
            setLoading(false);
        }
    };

    return (
        <BackgroundImage>
            <Container component="main" maxWidth="xs" sx={{ position: 'relative', zIndex: 1 }}>
                <Box
                    sx={{
                        minHeight: '100vh',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        py: 4,
                    }}
                >
                    <Typography
                        variant="h2"
                        component="h1"
                        sx={{
                            color: '#ffffff',
                            textAlign: 'center',
                            mb: 4,
                            fontWeight: 700,
                            textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
                            background: 'linear-gradient(45deg, #64B5F6 30%, #FF4081 90%)',
                            WebkitBackgroundClip: 'text',
                            WebkitTextFillColor: 'transparent',
                        }}
                    >
                        Sales Chatbot
                    </Typography>

                    <Typography
                        variant="h6"
                        sx={{
                            color: 'rgba(255, 255, 255, 0.9)',
                            textAlign: 'center',
                            mb: 6,
                            maxWidth: '600px',
                            textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
                        }}
                    >
                        Your AI-powered shopping assistant for seamless product management
                    </Typography>

                    <LoginCard elevation={3}>
                        <Box
                            sx={{
                                width: 80,
                                height: 80,
                                backgroundColor: 'primary.main',
                                borderRadius: '50%',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                margin: '0 auto 24px',
                                boxShadow: '0 4px 20px rgba(0,0,0,0.2)',
                            }}
                        >
                            <ShoppingCartIcon sx={{ fontSize: 40, color: 'white' }} />
                        </Box>

                        <Typography
                            component="h2"
                            variant="h5"
                            sx={{
                                color: '#ffffff',
                                textAlign: 'center',
                                mb: 3,
                                fontWeight: 600,
                            }}
                        >
                            {isLogin ? 'Welcome Back' : 'Create Account'}
                        </Typography>

                        <Tabs
                            value={isLogin ? 0 : 1}
                            onChange={(e, newValue) => setIsLogin(newValue === 0)}
                            centered
                            sx={{
                                mb: 3,
                                '& .MuiTab-root': {
                                    color: 'rgba(255, 255, 255, 0.7)',
                                    '&.Mui-selected': {
                                        color: '#ffffff',
                                    },
                                },
                                '& .MuiTabs-indicator': {
                                    backgroundColor: 'primary.main',
                                },
                            }}
                        >
                            <Tab label="Login" />
                            <Tab label="Register" />
                        </Tabs>

                        {error && (
                            <Alert
                                severity="error"
                                sx={{
                                    width: '100%',
                                    mb: 2,
                                    backgroundColor: 'rgba(211, 47, 47, 0.1)',
                                    color: '#ff6b6b',
                                    '& .MuiAlert-icon': {
                                        color: '#ff6b6b',
                                    },
                                }}
                            >
                                {error}
                            </Alert>
                        )}

                        <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
                            <StyledTextField
                                margin="normal"
                                required
                                fullWidth
                                id="username"
                                label="Username"
                                name="username"
                                autoComplete="username"
                                autoFocus
                                value={formData.username}
                                onChange={handleChange}
                            />
                            {!isLogin && (
                                <StyledTextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="email"
                                    label="Email"
                                    name="email"
                                    type="email"
                                    autoComplete="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                />
                            )}
                            <StyledTextField
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Password"
                                type="password"
                                id="password"
                                autoComplete={isLogin ? "current-password" : "new-password"}
                                value={formData.password}
                                onChange={handleChange}
                            />
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                sx={{
                                    mt: 3,
                                    mb: 2,
                                    height: 48,
                                    fontSize: '1.1rem',
                                    fontWeight: 600,
                                    background: 'linear-gradient(45deg, #64B5F6 30%, #FF4081 90%)',
                                    '&:hover': {
                                        background: 'linear-gradient(45deg, #42A5F5 30%, #F50057 90%)',
                                    },
                                }}
                                disabled={loading}
                            >
                                {loading ? (
                                    <CircularProgress size={24} color="inherit" />
                                ) : (
                                    isLogin ? 'Sign In' : 'Create Account'
                                )}
                            </Button>
                        </Box>
                    </LoginCard>
                </Box>
            </Container>
        </BackgroundImage>
    );
};

export default Login;
