import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
    Container,
    Box,
    TextField,
    Button,
    Typography,
    Paper,
    Alert,
    CircularProgress,
    Tabs,
    Tab
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { styled } from '@mui/material/styles';

const BackgroundImage = styled(Box)(({ theme }) => ({
    position: 'relative',
    minHeight: '100vh',
    backgroundImage: 'url(https://images.unsplash.com/photo-1607083206968-13611e3d76db?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80)',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        backdropFilter: 'blur(5px)',
    },
}));

const LoginCard = styled(Paper)(({ theme }) => ({
    position: 'relative',
    padding: theme.spacing(4),
    backgroundColor: 'rgba(30, 30, 30, 0.9)',
    backdropFilter: 'blur(10px)',
    borderRadius: '16px',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
    width: '100%',
    maxWidth: '400px',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    '& .MuiOutlinedInput-root': {
        backgroundColor: 'rgba(255, 255, 255, 0.05)',
        color: '#ffffff',
        '& fieldset': {
            borderColor: 'rgba(255, 255, 255, 0.1)',
        },
        '&:hover fieldset': {
            borderColor: 'rgba(255, 255, 255, 0.2)',
        },
        '&.Mui-focused fieldset': {
            borderColor: theme.palette.primary.main,
        },
    },
    '& .MuiInputLabel-root': {
        color: 'rgba(255, 255, 255, 0.7)',
    },
    '& .MuiInputBase-input': {
        color: '#ffffff',
    },
}));

const Login = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        email: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { login, user } = useAuth();
    const navigate = useNavigate();

    // Redirect if already logged in
    useEffect(() => {
        if (user) {
            navigate('/chat');
        }
    }, [user, navigate]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            if (isLogin) {
                const response = await fetch('http://localhost:5000/api/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ username: formData.username, password: formData.password }),
                    credentials: 'include'
                });

                if (response.ok) {
                    const data = await response.json();
                    login(data.user);
                    navigate('/chat');
                } else {
                    const data = await response.json();
                    setError(data.error || 'Login failed');
                }
            } else {
                const response = await fetch('http://localhost:5000/api/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(formData),
                });

                if (!response.ok) {
                    const data = await response.json();
                    throw new Error(data.error || 'Registration failed');
                }

                // After successful registration, log in the user
                await login(formData.username, formData.password);
                navigate('/chat');
            }
        } catch (err) {
            setError(err.message || 'An error occurred');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box
            sx={{
                minHeight: '100vh',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: '#1A1A1A',
                background: 'linear-gradient(135deg, #1A1A1A 0%, #2D2D2D 100%)',
                position: 'relative',
                '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'radial-gradient(circle at center, rgba(233, 30, 99, 0.1) 0%, rgba(33, 150, 243, 0.1) 100%)',
                    pointerEvents: 'none',
                },
            }}
        >
            <Container maxWidth="sm">
                <Paper
                    elevation={3}
                    sx={{
                        p: 4,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        bgcolor: 'rgba(45, 45, 45, 0.95)',
                        backdropFilter: 'blur(10px)',
                        border: '1px solid rgba(233, 30, 99, 0.2)',
                        borderRadius: 2,
                        position: 'relative',
                        '&::before': {
                            content: '""',
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'linear-gradient(45deg, rgba(233, 30, 99, 0.05) 0%, rgba(33, 150, 243, 0.05) 100%)',
                            borderRadius: 'inherit',
                            pointerEvents: 'none',
                        },
                    }}
                >
                    <Box
                        sx={{
                            width: 100,
                            height: 100,
                            mb: 3,
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            bgcolor: 'rgba(33, 150, 243, 0.9)',
                            borderRadius: '50%',
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                            border: '3px solid rgba(233, 30, 99, 0.3)',
                            position: 'relative',
                            '&::after': {
                                content: '""',
                                position: 'absolute',
                                top: -2,
                                left: -2,
                                right: -2,
                                bottom: -2,
                                borderRadius: '50%',
                                background: 'linear-gradient(45deg, rgba(233, 30, 99, 0.3) 0%, rgba(33, 150, 243, 0.3) 100%)',
                                zIndex: -1,
                            },
                        }}
                    >
                        <span role="img" aria-label="robot" style={{ fontSize: '3rem' }}>🤖</span>
                    </Box>
                    <Typography
                        component="h1"
                        variant="h4"
                        sx={{
                            mb: 3,
                            fontWeight: 600,
                            background: 'linear-gradient(45deg, #E91E63 30%, #2196F3 90%)',
                            WebkitBackgroundClip: 'text',
                            WebkitTextFillColor: 'transparent',
                            textAlign: 'center',
                        }}
                    >
                        Sales Chatbot
                    </Typography>
                    {error && (
                        <Alert 
                            severity="error" 
                            sx={{ 
                                width: '100%', 
                                mb: 2,
                                bgcolor: 'rgba(211, 47, 47, 0.1)',
                                color: '#FF8A80',
                                '& .MuiAlert-icon': {
                                    color: '#FF8A80',
                                },
                            }}
                        >
                            {error}
                        </Alert>
                    )}
                    <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="Username"
                            value={formData.username}
                            onChange={handleChange}
                            name="username"
                            sx={{
                                '& .MuiOutlinedInput-root': {
                                    bgcolor: 'rgba(255, 255, 255, 0.05)',
                                    color: '#FFFFFF',
                                    '& fieldset': {
                                        borderColor: 'rgba(233, 30, 99, 0.3)',
                                    },
                                    '&:hover fieldset': {
                                        borderColor: 'rgba(233, 30, 99, 0.5)',
                                    },
                                    '&.Mui-focused fieldset': {
                                        borderColor: '#E91E63',
                                    },
                                },
                                '& .MuiInputLabel-root': {
                                    color: 'rgba(255, 255, 255, 0.7)',
                                },
                            }}
                        />
                        {!isLogin && (
                            <TextField
                                margin="normal"
                                required
                                fullWidth
                                label="Email"
                                value={formData.email}
                                onChange={handleChange}
                                name="email"
                                type="email"
                                sx={{
                                    '& .MuiOutlinedInput-root': {
                                        bgcolor: 'rgba(255, 255, 255, 0.05)',
                                        color: '#FFFFFF',
                                        '& fieldset': {
                                            borderColor: 'rgba(233, 30, 99, 0.3)',
                                        },
                                        '&:hover fieldset': {
                                            borderColor: 'rgba(233, 30, 99, 0.5)',
                                        },
                                        '&.Mui-focused fieldset': {
                                            borderColor: '#E91E63',
                                        },
                                    },
                                    '& .MuiInputLabel-root': {
                                        color: 'rgba(255, 255, 255, 0.7)',
                                    },
                                }}
                            />
                        )}
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="Password"
                            type="password"
                            value={formData.password}
                            onChange={handleChange}
                            name="password"
                            sx={{
                                '& .MuiOutlinedInput-root': {
                                    bgcolor: 'rgba(255, 255, 255, 0.05)',
                                    color: '#FFFFFF',
                                    '& fieldset': {
                                        borderColor: 'rgba(233, 30, 99, 0.3)',
                                    },
                                    '&:hover fieldset': {
                                        borderColor: 'rgba(233, 30, 99, 0.5)',
                                    },
                                    '&.Mui-focused fieldset': {
                                        borderColor: '#E91E63',
                                    },
                                },
                                '& .MuiInputLabel-root': {
                                    color: 'rgba(255, 255, 255, 0.7)',
                                },
                            }}
                        />
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{
                                mt: 3,
                                mb: 2,
                                py: 1.5,
                                bgcolor: 'rgba(233, 30, 99, 0.9)',
                                '&:hover': {
                                    bgcolor: 'rgba(233, 30, 99, 1)',
                                },
                            }}
                            disabled={loading}
                        >
                            {loading ? (
                                <CircularProgress size={24} color="inherit" />
                            ) : (
                                isLogin ? 'Sign In' : 'Create Account'
                            )}
                        </Button>
                    </Box>
                </Paper>
            </Container>
        </Box>
    );
};

export default Login;
