import React, { useState, useRef, useEffect, useCallback } from 'react';
import {
    Box,
    TextField,
    Button,
    Paper,
    Typography,
    IconButton,
    CircularProgress,
    List,
    ListItem,
    ListItemText,
    Divider,
    AppBar,
    Toolbar,
    Menu,
    MenuItem,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import MicIcon from '@mui/icons-material/Mic';
import StopIcon from '@mui/icons-material/Stop';
import LogoutIcon from '@mui/icons-material/Logout';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const ChatInterface = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [isRecording, setIsRecording] = useState(false);
    const [anchorEl, setAnchorEl] = useState(null);
    const messagesEndRef = useRef(null);
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const recognitionRef = useRef(null);

    const formatVoiceCommand = (transcript) => {
        // Convert transcript to lowercase for easier matching
        const command = transcript.toLowerCase().trim();
        
        // Handle different command patterns
        if (command.includes('add product') || command.includes('create product')) {
            // Extract product details using regex
            const pattern = /(?:add|create) product:?\s*([^,]+),\s*(\d+\.?\d*),\s*(\d+),\s*([^,]+)/i;
            const match = command.match(pattern);
            if (match) {
                const [_, name, price, stock, category] = match;
                return `Add product: ${name.trim()}, ${price}, ${stock}, ${category.trim()}`;
            }
        }
        
        if (command.includes('update product') || command.includes('modify product')) {
            const pattern = /(?:update|modify) product:?\s*([^,]+),\s*([^,]+),\s*([^,]+)/i;
            const match = command.match(pattern);
            if (match) {
                const [_, name, field, value] = match;
                return `Update product: ${name.trim()}, ${field.trim()}, ${value.trim()}`;
            }
        }
        
        if (command.includes('delete product') || command.includes('remove product')) {
            const pattern = /(?:delete|remove) product:?\s*([^,]+)/i;
            const match = command.match(pattern);
            if (match) {
                return `Delete product: ${match[1].trim()}`;
            }
        }
        
        if (command.includes('search')) {
            const pattern = /search\s+(.+)/i;
            const match = command.match(pattern);
            if (match) {
                return `Search ${match[1].trim()}`;
            }
        }
        
        if (command.includes('category')) {
            const pattern = /category\s+(.+)/i;
            const match = command.match(pattern);
            if (match) {
                return `Category ${match[1].trim()}`;
            }
        }
        
        if (command.includes('show all') || command.includes('list all')) {
            return 'Show all products';
        }
        
        // If no specific command pattern is matched, return the original transcript
        return command;
    };

    const scrollToBottom = useCallback(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleMenuClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/login');
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    const handleSendMessage = useCallback(async (messageContent) => {
        if (!messageContent.trim()) return;

        const userMessage = {
            role: 'user',
            content: messageContent.trim()
        };

        setMessages(prev => [...prev, userMessage]);
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:5000/api/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message: userMessage.content }),
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                const botMessage = {
                    role: 'assistant',
                    content: data.response
                };
                setMessages(prev => [...prev, botMessage]);
            } else {
                throw new Error('Failed to get response');
            }
        } catch (error) {
            console.error('Error:', error);
            setMessages(prev => [...prev, {
                role: 'assistant',
                content: 'Sorry, I encountered an error. Please try again.'
            }]);
        } finally {
            setIsLoading(false);
        }
    }, []);

    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();
        if (!input.trim()) return;
        
        const currentInput = input;
        setInput('');
        await handleSendMessage(currentInput);
    }, [input, handleSendMessage]);

    useEffect(() => {
        // Initialize speech recognition
        if ('webkitSpeechRecognition' in window) {
            recognitionRef.current = new window.webkitSpeechRecognition();
            recognitionRef.current.continuous = false;
            recognitionRef.current.interimResults = false;
            recognitionRef.current.lang = 'en-US';

            recognitionRef.current.onresult = (event) => {
                const transcript = event.results[0][0].transcript;
                setInput(transcript);
            };

            recognitionRef.current.onend = () => {
                setIsRecording(false);
                if (input.trim()) {
                    handleSubmit(new Event('submit'));
                }
            };
        }

        // Load chat history when component mounts
        const loadChatHistory = async () => {
            try {
                // Clear any existing messages first
                setMessages([]);
                
                const response = await fetch('http://localhost:5000/api/chat/history', {
                    credentials: 'include'
                });
                
                if (response.ok) {
                    const data = await response.json();
                    if (data.messages && data.messages.length > 0) {
                        setMessages(data.messages);
                    } else {
                        // Show welcome message for new users
                        const welcomeMessage = {
                            role: 'assistant',
                            content: `ðŸŒŸ Welcome ${user?.username || 'there'} to the Sales Chatbot! ðŸŒŸ\n\n` +
                                'I\'m your AI-powered shopping assistant, ready to help you manage your products efficiently.\n\n' +
                                'ðŸŽ¯ Here\'s what I can do for you:\n\n' +
                                'ðŸ“¦ Product Management:\n' +
                                '   â€¢ Add new products\n' +
                                '   â€¢ Update existing products\n' +
                                '   â€¢ Remove products\n' +
                                '   â€¢ View all products\n\n' +
                                'ðŸ” Search & Filter:\n' +
                                '   â€¢ Search by product name\n' +
                                '   â€¢ Filter by category\n' +
                                '   â€¢ View product details\n\n' +
                                'ðŸŽ¤ Voice Commands:\n' +
                                '   â€¢ Click the microphone icon\n' +
                                '   â€¢ Speak your command clearly\n' +
                                '   â€¢ Click again to stop\n\n' +
                                'ðŸ’¡ Quick Tips:\n' +
                                '   â€¢ Use clear, specific commands\n' +
                                '   â€¢ Prices should be numbers\n' +
                                '   â€¢ Stock should be whole numbers\n' +
                                '   â€¢ Categories help organize products\n\n' +
                                'ðŸ“ Example Commands:\n' +
                                '   â€¢ "Add product: laptop, 999.99, 10, electronics"\n' +
                                '   â€¢ "Update product: laptop, price, 899.99"\n' +
                                '   â€¢ "Search laptop"\n' +
                                '   â€¢ "Show all products"\n\n' +
                                'How can I assist you today? ðŸ˜Š'
                        };
                        setMessages([welcomeMessage]);
                        // Save welcome message to history
                        await handleSendMessage(welcomeMessage.content);
                    }
                } else {
                    // If there's an error loading history, show welcome message
                    const welcomeMessage = {
                        role: 'assistant',
                        content: `ðŸŒŸ Welcome ${user?.username || 'there'} to the Sales Chatbot! ðŸŒŸ\n\n` +
                            'I\'m your AI-powered shopping assistant. How can I help you today?'
                    };
                    setMessages([welcomeMessage]);
                }
            } catch (error) {
                console.error('Error loading chat history:', error);
                // Show welcome message if there's an error loading history
                setMessages([{
                    role: 'assistant',
                    content: `ðŸŒŸ Welcome ${user?.username || 'there'} to the Sales Chatbot! ðŸŒŸ\n\n` +
                        'I\'m your AI-powered shopping assistant. How can I help you today?'
                }]);
            }
        };

        loadChatHistory();
    }, [user, handleSendMessage]);

    // Save chat history whenever messages change
    useEffect(() => {
        const saveChatHistory = async () => {
            if (messages.length > 0) {
                try {
                    await fetch('http://localhost:5000/api/chat/history', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ messages }),
                        credentials: 'include'
                    });
                } catch (error) {
                    console.error('Error saving chat history:', error);
                }
            }
        };

        saveChatHistory();
    }, [messages]);

    const handleVoiceInput = useCallback(() => {
        if (!recognitionRef.current) {
            alert('Speech recognition is not supported in your browser.');
            return;
        }

        if (isRecording) {
            recognitionRef.current.stop();
        } else {
            setInput('');
            recognitionRef.current.start();
            setIsRecording(true);
        }
    }, [isRecording]);

    return (
        <Box
            sx={{
                height: '100vh',
                display: 'flex',
                flexDirection: 'column',
                bgcolor: '#1A1A1A',
                background: 'linear-gradient(135deg, rgba(26, 26, 26, 0.9) 0%, rgba(45, 45, 45, 0.9) 100%), url("https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80")',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'relative',
                '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'radial-gradient(circle at center, rgba(233, 30, 99, 0.1) 0%, rgba(33, 150, 243, 0.1) 100%)',
                    pointerEvents: 'none',
                },
            }}
        >
            <AppBar 
                position="static" 
                sx={{ 
                    bgcolor: 'rgba(45, 45, 45, 0.95)',
                    backdropFilter: 'blur(10px)',
                    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.3)',
                    borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
                }}
            >
                <Toolbar>
                    <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
                        <Typography 
                            variant="h6" 
                            component="div" 
                            sx={{ 
                                fontWeight: 600,
                                background: 'linear-gradient(45deg, #FFB74D 30%, #FFE0B2 90%)',
                                WebkitBackgroundClip: 'text',
                                WebkitTextFillColor: 'transparent',
                                display: 'flex',
                                alignItems: 'center',
                                gap: 1,
                            }}
                        >
                            <span role="img" aria-label="robot" style={{ fontSize: '1.5rem' }}>ðŸ¤–</span>
                            Sales Chatbot
                        </Typography>
                    </Box>
                    <IconButton
                        color="inherit"
                        onClick={handleMenuClick}
                        sx={{ 
                            ml: 2,
                            color: '#FFB74D',
                            '&:hover': {
                                bgcolor: 'rgba(255, 183, 77, 0.1)',
                            },
                        }}
                    >
                        <AccountCircleIcon />
                    </IconButton>
                    <Menu
                        anchorEl={anchorEl}
                        open={Boolean(anchorEl)}
                        onClose={handleMenuClose}
                        sx={{
                            '& .MuiPaper-root': {
                                bgcolor: 'rgba(45, 45, 45, 0.95)',
                                backdropFilter: 'blur(10px)',
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                minWidth: '200px',
                            },
                        }}
                    >
                        <MenuItem onClick={handleMenuClose} disabled sx={{ color: '#FFE0B2' }}>
                            <AccountCircleIcon sx={{ mr: 1 }} />
                            {user?.username}
                        </MenuItem>
                        <Divider sx={{ my: 1, bgcolor: 'rgba(255, 255, 255, 0.1)' }} />
                        <MenuItem 
                            onClick={handleLogout}
                            sx={{
                                color: '#FFB74D',
                                '&:hover': {
                                    bgcolor: 'rgba(255, 183, 77, 0.1)',
                                },
                            }}
                        >
                            <LogoutIcon sx={{ mr: 1 }} />
                            Logout
                        </MenuItem>
                    </Menu>
                </Toolbar>
            </AppBar>

            <Paper
                elevation={3}
                sx={{
                    flex: 1,
                    display: 'flex',
                    flexDirection: 'column',
                    overflow: 'hidden',
                    bgcolor: 'rgba(26, 26, 26, 0.95)',
                    backdropFilter: 'blur(10px)',
                    borderRadius: 0,
                    position: 'relative',
                    '&::before': {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        background: 'radial-gradient(circle at top right, rgba(255, 183, 77, 0.1) 0%, transparent 60%)',
                        pointerEvents: 'none',
                    },
                }}
            >
                <List
                    sx={{
                        flex: 1,
                        overflow: 'auto',
                        p: 2,
                        '& > *': { mb: 2 },
                        '&::-webkit-scrollbar': {
                            width: '8px',
                        },
                        '&::-webkit-scrollbar-track': {
                            background: 'rgba(255, 255, 255, 0.05)',
                        },
                        '&::-webkit-scrollbar-thumb': {
                            background: 'rgba(255, 183, 77, 0.2)',
                            borderRadius: '4px',
                        },
                        '&::-webkit-scrollbar-thumb:hover': {
                            background: 'rgba(255, 183, 77, 0.3)',
                        },
                    }}
                >
                    {messages.map((message, index) => (
                        <ListItem
                            key={index}
                            sx={{
                                flexDirection: 'column',
                                alignItems: message.role === 'user' ? 'flex-end' : 'flex-start',
                            }}
                        >
                            <Box
                                sx={{
                                    display: 'flex',
                                    alignItems: 'flex-start',
                                    gap: 1,
                                    maxWidth: '80%',
                                }}
                            >
                                {message.role === 'assistant' && (
                                    <Box
                                        sx={{
                                            width: 32,
                                            height: 32,
                                            borderRadius: '50%',
                                            bgcolor: 'rgba(255, 183, 77, 0.9)',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            flexShrink: 0,
                                            mt: 1,
                                            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
                                        }}
                                    >
                                        <span role="img" aria-label="robot" style={{ fontSize: '1.2rem' }}>ðŸ¤–</span>
                                    </Box>
                                )}
                                <Paper
                                    elevation={1}
                                    sx={{
                                        p: 2,
                                        bgcolor: message.role === 'user' 
                                            ? 'rgba(233, 30, 99, 0.9)' 
                                            : 'rgba(33, 150, 243, 0.9)',
                                        color: '#FFFFFF',
                                        borderRadius: 2,
                                        border: '1px solid rgba(255, 255, 255, 0.1)',
                                        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
                                        position: 'relative',
                                        '&::before': {
                                            content: '""',
                                            position: 'absolute',
                                            top: 0,
                                            left: 0,
                                            right: 0,
                                            bottom: 0,
                                            background: 'linear-gradient(45deg, rgba(255,255,255,0.1) 0%, transparent 100%)',
                                            borderRadius: 'inherit',
                                            pointerEvents: 'none',
                                        },
                                    }}
                                >
                                    <Typography
                                        variant="body1"
                                        sx={{
                                            whiteSpace: 'pre-line',
                                            fontFamily: 'Consolas, monospace',
                                            fontSize: '0.95rem',
                                            lineHeight: 1.6,
                                            color: message.role === 'user' 
                                                ? '#FFFFFF' 
                                                : '#F5F5F5',
                                            '& strong': {
                                                color: '#FFB74D',
                                                fontWeight: 600,
                                            },
                                            '& em': {
                                                color: '#FFE0B2',
                                                fontStyle: 'italic',
                                            },
                                            '& .emoji': {
                                                fontSize: '1.2em',
                                                margin: '0 2px',
                                            },
                                            '& .command': {
                                                color: '#FFB74D',
                                                fontFamily: 'Consolas, monospace',
                                                backgroundColor: 'rgba(255, 183, 77, 0.1)',
                                                padding: '2px 4px',
                                                borderRadius: '4px',
                                            },
                                            '& .highlight': {
                                                color: '#FFE0B2',
                                                fontWeight: 500,
                                            },
                                        }}
                                    >
                                        {message.content}
                                    </Typography>
                                </Paper>
                                {message.role === 'user' && (
                                    <Box
                                        sx={{
                                            width: 32,
                                            height: 32,
                                            borderRadius: '50%',
                                            bgcolor: 'rgba(255, 183, 77, 0.9)',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            flexShrink: 0,
                                            mt: 1,
                                            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
                                        }}
                                    >
                                        <AccountCircleIcon sx={{ color: '#FFFFFF' }} />
                                    </Box>
                                )}
                            </Box>
                        </ListItem>
                    ))}
                    <div ref={messagesEndRef} />
                </List>

                <Divider sx={{ bgcolor: 'rgba(255, 255, 255, 0.1)' }} />

                <Box
                    sx={{
                        p: 2,
                        bgcolor: 'rgba(45, 45, 45, 0.95)',
                        display: 'flex',
                        gap: 1,
                        borderTop: '1px solid rgba(255, 255, 255, 0.1)',
                        position: 'relative',
                        '&::before': {
                            content: '""',
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            height: '1px',
                            background: 'linear-gradient(90deg, transparent, rgba(255, 183, 77, 0.2), transparent)',
                        },
                    }}
                >
                    <TextField
                        fullWidth
                        variant="outlined"
                        placeholder="Type your message..."
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={handleSubmit}
                        disabled={isLoading}
                        sx={{
                            '& .MuiOutlinedInput-root': {
                                bgcolor: 'rgba(255, 255, 255, 0.05)',
                                color: '#FFFFFF',
                                '& fieldset': {
                                    borderColor: 'rgba(233, 30, 99, 0.3)',
                                },
                                '&:hover fieldset': {
                                    borderColor: 'rgba(233, 30, 99, 0.5)',
                                },
                                '&.Mui-focused fieldset': {
                                    borderColor: '#E91E63',
                                },
                            },
                            '& .MuiInputLabel-root': {
                                color: 'rgba(255, 255, 255, 0.7)',
                            },
                        }}
                    />
                    <IconButton
                        onClick={handleVoiceInput}
                        color={isRecording ? 'error' : 'primary'}
                        sx={{ 
                            bgcolor: 'rgba(255, 255, 255, 0.05)',
                            color: '#FFB74D',
                            '&:hover': {
                                bgcolor: 'rgba(255, 183, 77, 0.1)',
                            },
                        }}
                    >
                        {isRecording ? <StopIcon /> : <MicIcon />}
                    </IconButton>
                    <Button
                        variant="contained"
                        onClick={handleSubmit}
                        disabled={isLoading || !input.trim()}
                        endIcon={isLoading ? <CircularProgress size={20} /> : <SendIcon />}
                        sx={{
                            minWidth: '100px',
                            bgcolor: 'rgba(255, 183, 77, 0.9)',
                            '&:hover': {
                                bgcolor: 'rgba(255, 183, 77, 1)',
                            },
                            '&.Mui-disabled': {
                                bgcolor: 'rgba(255, 255, 255, 0.1)',
                            },
                        }}
                    >
                        {isLoading ? 'Sending...' : 'Send'}
                    </Button>
                </Box>
            </Paper>
        </Box>
    );
};

export default ChatInterface;
