import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Card,
  CardContent,
  TextField,
  Button,
  Typography,
  Tab,
  Tabs,
  Alert,
  Paper,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { useAuth } from '../contexts/AuthContext';

const BackgroundImage = styled(Paper)(({ theme }) => ({
  position: 'relative',
  backgroundColor: theme.palette.grey[900],
  color: theme.palette.common.white,
  marginBottom: theme.spacing(4),
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
  backgroundPosition: 'center',
  backgroundImage: 'url(https://images.unsplash.com/photo-1607083206968-13611e3d76db?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80)',
  height: '250px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(45deg, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0.4) 100%)',
  },
}));

const BackgroundText = styled(Box)(({ theme }) => ({
  position: 'relative',
  padding: theme.spacing(3),
  textAlign: 'center',
  [theme.breakpoints.up('md')]: {
    padding: theme.spacing(6),
  },
}));

const LoginCard = styled(Card)(({ theme }) => ({
  maxWidth: '400px',
  margin: '0 auto',
  borderRadius: '24px',
  boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
  backdropFilter: 'blur(10px)',
  backgroundColor: 'rgba(30, 30, 30, 0.9)',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  transform: 'translateY(-50px)',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
  '& .MuiOutlinedInput-root': {
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    color: '#ffffff',
    '& fieldset': {
      borderColor: 'rgba(255, 255, 255, 0.1)',
    },
    '&:hover fieldset': {
      borderColor: 'rgba(255, 255, 255, 0.2)',
    },
    '&.Mui-focused fieldset': {
      borderColor: theme.palette.primary.main,
    },
  },
  '& .MuiInputLabel-root': {
    color: 'rgba(255, 255, 255, 0.7)',
  },
  '& .MuiInputBase-input': {
    color: '#ffffff',
  },
}));

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (isLogin) {
        await login(formData.username, formData.password);
        navigate('/');
      } else {
        const response = await fetch('http://localhost:5000/api/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });

        if (!response.ok) {
          const data = await response.json();
          throw new Error(data.error || 'Registration failed');
        }

        // After successful registration, log in the user
        await login(formData.username, formData.password);
        navigate('/');
      }
    } catch (err) {
      setError(err.message || 'An error occurred');
    }
  };

  return (
    <Container maxWidth="sm" sx={{ py: 2, minHeight: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
      <BackgroundImage>
        <BackgroundText>
          <Typography
            component="h1"
            variant="h3"
            color="inherit"
            gutterBottom
            sx={{ 
              fontWeight: 900,
              textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
              letterSpacing: '2px',
              background: 'linear-gradient(45deg, #64B5F6 30%, #FF4081 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          >
            Sales Chatbot
          </Typography>
          <Typography 
            variant="h6" 
            color="inherit" 
            paragraph
            sx={{
              textShadow: '1px 1px 2px rgba(0,0,0,0.3)',
              maxWidth: '600px',
              margin: '0 auto',
              opacity: 0.9,
            }}
          >
            Your AI-powered shopping assistant
          </Typography>
        </BackgroundText>
      </BackgroundImage>

      <LoginCard elevation={3}>
        <CardContent sx={{ p: 3 }}>
          <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
            <Tabs
              value={isLogin ? 0 : 1}
              onChange={(e, newValue) => setIsLogin(newValue === 0)}
              centered
              sx={{
                '& .MuiTab-root': {
                  fontSize: '1rem',
                  fontWeight: 600,
                  textTransform: 'none',
                  minWidth: 100,
                  transition: 'all 0.3s ease',
                  color: 'rgba(255, 255, 255, 0.7)',
                  '&.Mui-selected': {
                    color: '#ffffff',
                  },
                  '&:hover': {
                    color: 'primary.main',
                  },
                },
                '& .MuiTabs-indicator': {
                  backgroundColor: 'primary.main',
                },
              }}
            >
              <Tab label="Login" />
              <Tab label="Register" />
            </Tabs>
          </Box>

          {error && (
            <Alert 
              severity="error" 
              sx={{ 
                mb: 2,
                borderRadius: 2,
                backgroundColor: 'rgba(211, 47, 47, 0.1)',
                '& .MuiAlert-message': {
                  fontSize: '0.9rem',
                  color: '#ffffff',
                },
              }}
            >
              {error}
            </Alert>
          )}

          <form onSubmit={handleSubmit}>
            <StyledTextField
              fullWidth
              label="Username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              margin="normal"
              required
              size="small"
            />
            {!isLogin && (
              <StyledTextField
                fullWidth
                label="Email"
                name="email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                margin="normal"
                required
                size="small"
              />
            )}
            <StyledTextField
              fullWidth
              label="Password"
              name="password"
              type="password"
              value={formData.password}
              onChange={handleChange}
              margin="normal"
              required
              size="small"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              size="large"
              sx={{ 
                mt: 3,
                py: 1,
                fontSize: '1rem',
                fontWeight: 600,
                borderRadius: 2,
                textTransform: 'none',
                background: 'linear-gradient(45deg, #64B5F6 30%, #FF4081 90%)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #42A5F5 30%, #F50057 90%)',
                },
              }}
            >
              {isLogin ? 'Login' : 'Register'}
            </Button>
          </form>
        </CardContent>
      </LoginCard>
    </Container>
  );
};

export default Login;
